def handle_click():
    global entry_1, entry_2, entry_3, entry_4, entry_5, entry_6  # '2023-02-17T10:02:13Z'

    if entry_3.get() not in ['Run', 'MountainBikeRide', 'Ride', 'Walk', 'Workout']: return None
    entry2_formatted = entry_2.get() + 'T17:00:00Z'
    if re.search("[0-9]{1}:[0-9]{2}", entry_6.get()) is not None or \
            re.search("[0-9]{2}:[0-9]{2}", entry_6.get()) is not None or\
            re.search("[0-9]{1}:[0-9]{2}:[0-9]{2}", entry_6.get()) is not None or \
            re.search("[0-9]{2}:[0-9]{2}:[0-9]{2}", entry_6.get()) is not None or int(entry_6.get()) == 0:
        reversed_list = [i for i in re.split(":", entry_6.get())]
        reversed_list.reverse()
        entry6_formatted = sum([int(60**j) * int(reversed_list[j]) for j in range(len(reversed_list))])
    else: return None
    entry1_formatted = f'{float(entry_1.get())*1000: .2f}'
    print(entry1_formatted)
    writeP = {"name": entry_4.get(), "sport_type": entry_3.get(), "start_date_local": entry2_formatted,
              "elapsed_time": entry6_formatted, "distance": entry1_formatted}
    print(writeP)
    PythonWrite.writeToApi(writeP)
    entry_1.delete(0, tkinter.END)
    entry_2.delete(0, tkinter.END)
    entry_6.delete(0, tkinter.END)


